mapper.insert:
insert into ks_answer(answerID,answerSheetID,questionID,isReview,score,correct,respondence) select ks_answer_SEQ.nextval as answerID, A.* from (
select #{item.answerSheetID,jdbcType=INTEGER} answerSheetID, #{item.questionID,jdbcType=INTEGER} questionID, #{item.isReview,jdbcType=INTEGER} isReview, #{item.score,jdbcType=INTEGER} score, #{item.correct,jdbcType=INTEGER} correct, #{item.respondence,jdbcType=VARCHAR} respondence from dual
) A

mapper.update:
	begin  



            update ks_answer 

	                score = #{item.score},
					isReview = 1,	

            where answerID = #{item.answerID}



	;end;
