mapper.update:
update ks_answersheet set
startTime = #{startTime,jdbcType=DATE}, assignmentTime = #{assignmentTime,jdbcType=DATE}, addressIP = #{addressIP,jdbcType=VARCHAR}, isMarking = #{isMarking}, score = #{score,jdbcType=FLOAT}, isPass = #{isPass},
where answerSheetID =#{answerSheetID}

sql.where:
a.userid = b.userid and  a.examid = #{examID,jdbcType=INTEGER}
and b.jh = #{jh,jdbcType=INTEGER}
and b.xm = #{xm,jdbcType=VARCHAR}
